</div>

<h1>Node.js</h1>

<div id="myProgress">
  <div id="nodejs" class="myBar">70%</div>
</div>

<h1>Express</h1>

<div id="myProgress">
  <div id="express" class="myBar">70%</div>
</div>


</div>)




};


var i = 0;
function move(Prog, Lang) {
  i=0
  if (i == 0) {
    i = 1;
    var elem = document.getElementById(Lang);
    var width = 1;
    var id = setInterval(frame, 30);
    function frame() {
      if (width >= Prog) {
        clearInterval(id);
        i = 0;
      } else {
        width++;
        elem.style.width = width + "%";
      }
    }
  }
}


export default About;
export { move }
