import React from "react"



function About(){
  var i = 0;




  return(

<div><h1>HTML</h1>

<div id="myProgress">
  <div id="myBar">0%</div>
</div>
</div>)
    if (i == 0) {
      i = 1;
      var elem = document.getElementById("myBar");
      var width = 10;
      var id = setInterval(frame, 10);
      function frame() {
        if (width >= 100) {
          clearInterval(id);
          i = 0;
        } else {
          width++;
          elem.style.width = width + "%";
          elem.innerHTML = width  + "%";
        }
      }
    }
};


export default About;
